import React from "react";
import { useParams, Link } from "react-router-dom";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import Card from "./Card"; // Update the path to your Card component
import Back from "./Images/Back.png";
import NeonBack from "./Images/NeonBack.png";
import Promo from "./Images/Promo1.png";
import Promo2 from "./Images/Protein.png";
import Shoe from "./Images/Shoe.png";
import Bottle from "./Images/Bottle.png";
import Equipment from "./Images/Equipment.png";
import Footer from "./Images/Footer.png";

function SingleProduct({ cards }) {
  const { productId } = useParams();
  const product = cards.find((card) => card._id === productId);

  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div className="single-product">
      <img src={product.imgSrc} alt={product.title} />
      <h1>{product.title}</h1>
      <p>{product.description}</p>
      <p>Sizes: {product.sizes.join(", ")}</p>
      <Link to="/">Back to Products</Link>
    </div>
  );
}

function Product() {
  // Your card data
  const cards1 = [
    {
      _id: "1",
      imgSrc: require("./Images/Nike.png"),
      title: "Nike Dunk High",
      description: "Nike Dunk High is a high-top version...",
      sizes: ["38", "40", "42", "44"],
      link: "LionFit/lionfit/src/Pages/singleproducts.js",
    },
    // Add more card data as needed
  ];

  const cards2 = [
    {
      _id: "2",
      imgSrc: require("./Images/Recharge.png"),
      title: "Legion Recharge",
      description: "Legion Recharge is a high-quality supplement...",
      sizes: ["80", "100", "250"],
      link: "LionFit/lionfit/src/Pages/singleproducts.js",
    },
    // Add more card data as needed
  ];

  const cards3 = [
    {
      _id: "3",
      imgSrc: require("./Images/CreatineProtein.png"),
      title: "Body Attack",
      description: "Body Attack is a protein supplement...",
      sizes: ["80", "100", "200"],
      link: "LionFit/lionfit/src/Pages/singleproducts.js",
    },
    // Add more card data as needed
  ];

  return (
    <div className="Body">
      {/* ... (your carousel and other content) ... */}

      {/* Render the cards1 */}
      <div>
        <div style={{ width: "auto" }}>
          <h1 className="ShopT1">Who Gyms Without Shoes?</h1>
          <img
            style={{ width: "700px", marginLeft: "550px", marginTop: "-320px" }}
            src={Promo}
            alt="Promo"
          />
        </div>

        <div className="container">
          {cards1.map((card) => (
            <Link
              key={card._id}
              to={`/productSingle/${card._id}`}
              className="product-link"
            >
              <Card
                imgSrc={card.imgSrc}
                title={card.title}
                description={card.description}
                sizes={card.sizes}
                link={card.link}
              />
            </Link>
          ))}
        </div>

        <button className="button5">VIEW MORE</button>

        {/* ... (your other content) ... */}
      </div>

      {/* Render the single product page */}
      <Route path="/productSingle/:productId">
        <SingleProduct cards={[...cards1, ...cards2, ...cards3]} />
      </Route>
    </div>
  );
}

export default Product;
