import React from "react";
import { useLocation } from "react-router-dom"; // Import useLocation from React Router
import Background from '../Pages/Images/Back.png';

function Orders() {
//   const location = useLocation();
//   console.log("Location state:", location.state);
//   const cartItems = location.state ? location.state.cartItems : [];


  const removeFromCart = (productId) => {
    // Filter out the product with the specified ID from the cart
    const updatedCart = cartItems.filter((item) => item.id !== productId);

    // Update session storage and state
    sessionStorage.setItem("cart", JSON.stringify(updatedCart));
    setCartItems(updatedCart);
  };

  return (
    <div
      className="cartBack"
      style={{
        backgroundImage: `url(${Background})`,
        backgroundSize: "1481px",
        backgroundRepeat: "no-repeat",
        height: "3125px",
        backgroundColor: "black",
      }}
    >
      <div>
        <h1 className="TitleOrders">Your Orders</h1>
        <ul>
          {cartItems.map((item) => (
            <li key={item.id}>
                <img
                style={{ width: "300px", marginTop: "50px" }}
                src={item.image}
                alt={item.name}
              />
              <h2>Product Name: {item.name}</h2>
              <p>Category: {item.category}</p>
              <p>Price: {item.price}</p>
              <button
                  className="removeCart"
                  onClick={() => removeFromCart(item.id)}
                >
                  Remove
                </button>
              {/* Add more order-related information here */}
            </li>
            
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Orders;
