import React from "react";
import { useParams } from "react-router-dom";

function ProductSingle() {
  const { productIndex } = useParams();

  // Convert productIndex to a number (it comes as a string)
  const index = parseInt(productIndex, 10);

  // Retrieve the corresponding product based on the index
  const product = cards1[index];

  if (!product) {
    return <div>Product not found</div>; // Handle product not found
  }

  return (
    <div className="single-product">
      <h1>{product.title}</h1>
      <img src={product.imgSrc} alt={product.title} />
      <p>{product.description}</p>
      <p>Sizes: {product.sizes.join(", ")}</p>
      {/* Add more product details as needed */}
    </div>
  );
}

export default ProductSingle;
