import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import '../App.css';

function ProductSingle() {
  const { productId } = useParams(); // Get the product ID from the URL params
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // You would typically fetch the product details from an API or database based on the productId.
    // For this example, we'll use a hardcoded product object.
    const hardcodedProduct = {
      id: 1,
      name: "Sample Product",
      description: "This is a sample product description.",
      price: "$19.99",
      imageUrl: "sample-product-image.jpg",
    };

    // Simulate fetching product details by setting the product state.
    setProduct(hardcodedProduct);
  }, [productId]);

  return (
    <div className="single-product-container">
      {product ? (
        <>
          <div className="product-image">
            <img src={product.imageUrl} alt={product.name} />
          </div>
          <div className="product-details">
            <h1>{product.name}</h1>
            <p>{product.description}</p>
            <p>{product.price}</p>
            {/* Add any other product details you want to display */}
          </div>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default ProductSingle;
