import Card from './card';
import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';

function SingleProduct() {

  const { id } = useParams();

  const [product, setProduct] = useState()

  useEffect(() => {
    axios.get('http://localhost:5000/api/form/${id}')
    .then(res => console.log(res))
    .catch(err => console.log(err))
  },[])

  return (
    <div className='SinglePage'>
      <div className="container4">
        <img className='ProductSingle' src={card.imgSrc} alt={card.title} />
        <div>
          <h1 className='TitleSingle'>{card.title}</h1>
          <p className='NikeAd'>{card.description}</p>
          <p className='NikeDiscription'>Weight: {card.weight} ounces <br /> Rating: {card.rating} out of 5</p>
        </div>
      </div>
      <button className="button9">BACK</button>
      <button style={{ marginTop: '-250px' }} className="button8">PURCHASE</button>

      <h1 style={{ fontSize: '40px', marginTop: '160px', marginLeft: '-700px' }} className="neon-text">SIMILAR PRODUCTS</h1>
      <div className="container">
      {cards1.map((card, index) => (
            <Card
              key={index}
              imgSrc={card.imgSrc}
              title={card.title}
              description={card.description}
              sizes={card.sizes}
              link={card.link}
            />
          ))}
      </div>
    </div>
  );
}

export default SingleProduct;
