import React from "react";
import { useParams } from "react-router-dom";

function ProductSingle() {
  const { productIndex } = useParams();

  // Convert productIndex to a number (it comes as a string)
  const index = parseInt(productIndex, 10);

  // Define the sample product data array
  const cards1 = [
    {
      title: "Product 1",
      imgSrc: "image1.jpg",
      description: "Description of Product 1",
      sizes: ["Small", "Medium", "Large"],
    },
    {
      title: "Product 2",
      imgSrc: "image2.jpg",
      description: "Description of Product 2",
      sizes: ["Small", "Medium"],
    },
    // Add more product objects as needed
  ];

  // Retrieve the corresponding product based on the index
  const product = cards1[index];

  if (!product) {
    return <div>Product not found</div>; // Handle product not found
  }

  return (
    <div className="single-product" style={{ backgroundColor: "#E1E1E1" }}>
      <h1>{product.title}</h1>
      <img src={product.imgSrc} alt={product.title} />
      <p>{product.description}</p>
      <p>Sizes: {product.sizes.join(", ")}</p>
      {/* Add more product details as needed */}
    </div>
  );
}

export default ProductSingle;
