import React from "react";
import { useParams } from "react-router-dom"; // You would need to install and set up React Router if not done already
import '../App.css';

function SingleProduct({ products }) {
  const { productId } = useParams(); // Assuming you're passing the productId as a URL parameter

  const product = products.find(product => product.link === productId);

  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div className="single-product-container">
      <div className="single-product-image">
        <img src={product.imgSrc.default} alt={product.title} />
      </div>
      <div className="single-product-details">
        <h2>{product.title}</h2>
        <p>{product.description}</p>
        <p>Sizes available: {product.sizes.join(", ")}</p>
        {/* Add more product details here */}
      </div>
    </div>
  );
}

export default SingleProduct;
