import React from "react";
import Card from './card';
import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';

function SingleProduct() {

  const { id } = useParams();

  const [card, setCard] = useState({})

  useEffect(() => {
    axios.get(`http://localhost:5000/api/form/${id}`)
    .then(res => setCard(res.data))
    .catch(err => console.log(err))
  },[])

  return (
    <div className='SinglePage'>
      <div className="container4">
        <img className='ProductSingle' src={card.image} alt={card.name} />
        <div>
          <h1 className='TitleSingle'>{card.name}</h1>
          <p className='NikeAd'>{card.category}</p>
          <p className='NikeDiscription'>Price: {card.price} ounces <br /> Rating: {card.stock} out of 5</p>
        </div>
      </div>
      <button className="button9">ADD TO CART</button>
      <button className="button8">PURCHASE</button>

    </div>

    
  );
}

export default SingleProduct;
