import React, { useState } from 'react';
import axios from 'axios';
import '../App.css'; // Ensure that your CSS file is correctly imported
import Back from './Images/Back.png';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/login', { email, password });
      const { token } = response.data;

      // Store the token in localStorage (not secure, use HttpOnly cookies in production)
      localStorage.setItem('token', token);

      // Redirect to another page upon successful login
      // Replace '/dashboard' with your desired destination
      window.location.href = '/dashboard';
    } catch (error) {
      console.error('Login error:', error);
    }
  };

  return (
    <div
      style={{
        backgroundImage: `url(${Back})`,
        backgroundSize: '100%',
        backgroundColor: 'black',
        position: 'relative',
        height: '723px',
      }}
    >
      <div
        style={{ width: '100%' }}
        className="container5"
        id="container5"
      >
        <div className="form-container5 sign-in-container5">
          <form className="form2" onSubmit={handleSubmit}>
            <h1>Sign in</h1>
            <span>or use your account</span>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <a href="#">Forgot your password?</a>
            <button type="submit" className="SignUp">
              Sign In
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
